ad -p "Enter Notes: " notes ; echo "=============================================================\nNotes: \n$notes" >> "/media/Program13/13/truecrypt1/casefile/$case" ; gpg -r Ender --encrypt-files "/media/Program13/13/truecrypt1/casefile/$case" ; rm "/media/Program13/13/truecrypt1/casefile/$case" ;

